var a = msg.payload;
const newMsg = {};

newMsg.time = a.substr(0, 19);
newMsg.ID = a.substring( a.indexOf("'") + 1, a.indexOf(":",21));
newMsg.name = a.substring(a.indexOf(":",21)+1, a.indexOf("(",21));
newMsg.msgType = a.substring(a.indexOf(")' '")+4, a.indexOf(":",a.indexOf(")' '")+1));
newMsg.message = a.substring(a.indexOf("' '" + newMsg.msgType + ": "), a.lastIndexOf("'"));
newMsg.message = newMsg.message.replace("' '"+newMsg.msgType+": ","");

if (newMsg.msgType == "Global") {
  newMsg.payload = "```md\n# Time: [" + newMsg.time + "] ID: [" + newMsg.ID + "] Name: [" + newMsg.name + "] Type: [" + newMsg.msgType + "] Message: [" + newMsg.message +"]\n```";
} else if (newMsg.msgType == "Local"){
    newMsg.payload = "```\n# Time: [" + newMsg.time + "] ID: [" + newMsg.ID + "] Name: [" + newMsg.name + "] Type: [" + newMsg.msgType + "] Message: [" + newMsg.message +"]\n```";
} else {
    newMsg.payload = "```diff\n! Time: [" + newMsg.time + "] ID: [" + newMsg.ID + "] Name: [" + newMsg.name + "] Type: [" + newMsg.msgType + "] Message: [" + newMsg.message +"]\n```";
}

